import { useState, useEffect } from 'react'
import Title from './components/Title'
import QuestionsBlock from './components/QuestionsBlock'

const App = () => {
    const [quiz, setQuiz] = useState(false)
    const [chosenAnswerItems, setChosenAnswerItems] = useState([])
    const [unasnweredQuestionIds, setUnansweredQuestionIds] = useState([])


    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8000/quiz');
        const json = await response.json()
        setQuiz(json)
      } catch (err) {
        console.log(err)
      }
    }

    useEffect(() => {
      fetchData() 
    }, [])

    useEffect(() => {
      const unansweredIds = quiz?.content.map(({ id }) => id)
      setUnansweredQuestionIds(unansweredIds)
    }, [])

    console.log(chosenAnswerItems)

    console.log(unasnweredQuestionIds)

  return (
    <div  className="app">
      <Title title={quiz?.title} subtitle={quiz?.subtitle}/>
        {quiz && quiz?.content.map(contentItem => (
          <QuestionsBlock 
            key={contentItem.id}
            quizItem={contentItem} 
            setChosenAnswerItems={setChosenAnswerItems}
            chosenAnswerItems={chosenAnswerItems}
            setUnansweredQuestionIds={setUnansweredQuestionIds}
            unasnweredQuestionIds={unasnweredQuestionIds}
          />
        ))}

    </div>
  );
}

export default App;
